INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93732, 'Duloxetine', 'Pimozide', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may increase the plasma concentrations of pimozide, which is partially metabolized by the isoenzyme. The use of pimozide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', NULL, 'Due to the narrow therapeutic index of pimozide, concomitant use of pimozide in combination with moderate inhibitors of CYP450 2D6 should preferably be avoided if possible.', NULL, 'Due to the narrow therapeutic index of pimozide, concomitant use of pimozide in combination with moderate inhibitors of CYP450 2D6 should preferably be avoided if possible. Otherwise, close monitoring for the development of electrocardiographic abnormalities, extrapyramidal reactions (i.e., acute dystonic reactions, tardive dyskinesia, akathisia, Parkinson-like symptoms), and hypotension is recommended if concurrent use is required. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope. The manufacturers consider the use of pimozide with potent CYP450 2D6 inhibitors such as fluoxetine, paroxetine, and quinidine to be contraindicated.', 'Metabolism', 'Oxitriptan, Vilazodone, Vortioxetine, Tryptophan', 'Amisulpride, Pimavanserin', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/inte