INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93772, 'Duloxetine', 'Risperidone', 'Moderate', 'Duloxetine is a moderate inhibitor of CYP450 2D6 and may increase the plasma concentrations of drugs that are substrates of the isoenzyme. Conversely, many CYP450 2D6 substrates can also be competitive or noncompetitive inhibitors of the isoenzyme and may increase the plasma concentrations of duloxetine, which is partially metabolized by CYP450 2D6.', NULL, 'Caution is advised if duloxetine must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range such as tricyclic antidepressants, phenothiazines, beta blockers, and class IC antiarrhythmic agents (e.', NULL, 'Caution is advised if duloxetine must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range such as tricyclic antidepressants, phenothiazines, beta blockers, and class IC antiarrhythmic agents (e.g., propafenone, flecainide). A lower initial dosage, as well as clinical and laboratory monitoring, may be appropriate for some drugs.', 'Metabolism', 'Oxitriptan, Vilazodone, Vortioxetine, Tryptophan', 'Amisulpride, Pimavanserin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93876/', '[1] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[2] Skinner MH, Kuan HY, Pan A, et al. "Duloxetine is both an inhibitor and a substrate of cytochrome P4502D6 in healthy volunteers." Clin Pharmacol Ther 73 (2003): 170-7[3] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93773, 'Duloxetine', 'Ritodrine', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Paroxetine, St. John''s Wort, Nefazodone, Oxitriptan, Viloxazine, Vortioxetine, Tryptophan, Vilazodone, Esketamine, Fluvoxamine', 'Bremelanotide, Cabergoline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93877/', '[1] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[4] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philad