    ) VALUES (1380094, 'Carbamazepine', 'Methylprednisolone', 'Moderate', 'Carbamazepine may decrease the plasma concentrations and systemic effects of both endogenous and exogenous corticosteroids. The mechanism is accelerated corticosteroid metabolism due to induction of the CYP450 3A4 enzymatic pathway by carbamazepine.', NULL, 'Patients treated concomitantly with carbamazepine may require higher dosages of corticosteroids or adrenocorticotropic agents.', NULL, 'Patients treated concomitantly with carbamazepine may require higher dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever carbamazepine is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
