        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380101, 'Carbamazepine', 'Midazolam', 'Moderate', 'Some antiepileptic agents such as carbamazepine and phenytoin significantly reduce the effect of oral midazolam. The mechanism may be due to enhanced gut and liver metabolism of midazolam (via CYP450 3A4 enzyme induction by carbamazepine). Parenteral midazolam is not likely to be affected. Other oral benzodiazepines metabolized by the 3A4 isoenzyme may interact similarly.', NULL, 'Patients receiving this combination should be monitored for clinical response.', NULL, 'Patients receiving this combination should be monitored for clinical response. Alternative oral sedative hypnotics may be preferable in patients receiving carbamazepine.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
