        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380114, 'Carbamazepine', 'Naldemedine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G. Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.', NULL, 'Concomitant use of naldemedine with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, 'Concomitant use of naldemedine with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
