INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380116, 'Carbamazepine', 'Naltrexone', 'Moderate', 'Coadministration of naltrexone with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Naltrexone, especially in larger than recommended doses (more than 50 mg/day), has been associated with hepatocellular injury, hepatitis, and elevations in liver transaminases and bilirubin. Other potential causative or contributory etiologies identified include preexisting alcoholic liver disease, hepatitis B and/or C infection, and concomitant usage of other hepatotoxic drugs.', NULL, 'The use of naltrexone with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of naltrexone with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Periodic monitoring of hepatic function is advisable.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380117, 'Carbamazepine', 'Nefazodone', 'Major', 'Coadministration of carbamazepine and nefazodone may result in significantly decreased plasma concentrations of nefazodone and its active metabolite, hydroxynefazodone, and increased plasma concentrations of carbamazepine. The proposed mechanism is carbamazepine induction of nefazodone and hydroxynefazodone metabolism via CYP450 3A4, and nefazodone inhibition of carbamazepine metabolism via the same isoenzyme.', NULL, 'Concomitant use of nefazodone and carbamazepine is considered contraindicated.', NULL, 'Concomitant use of nefazodone and carbamazepine is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380118, 'Carbamazepine', 'Nelfinavir', 'Moderate', 'Coadministration of nelfinavir and carbamazepine may result in decreased plasma concentrations of nelfinavir and increased plasma concentrations of carbamazepine. The proposed mechanism involves carbamazepine induction of nelfinavir metabolism via CYP450 3A4 and, conversely, nelfinavir inhibition of carbamazepine metabolism via the same enzymatic pathway.', NULL, 'Given the lack of pharmacokinetic data and the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, it may be preferable to avoid using nelfinavir in combination with carbamazepine.', NULL, 'Given the lack of pharmacokinetic data and the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, it may be preferable to avoid using nelfinavir in combination with carbamazepine. Caution is advised if concomitant use is required. In patients already receiving nelfinavir as part of their HIV treatment regimen, close clinical and laboratory monitoring of antiretroviral response is recommended following the addition or discontinuation of carbamazepine. Likewise, if nelfinavir is added to stable carbamazepine therapy, serum drug levels and pharmacologic effects should be closely monitored and the carbamazepine dosage adjusted accordingly. Patients should be advised to contact their doctor if they develop signs of carbamazepine toxicity such as ataxia, disorientation, dizziness, nausea, vomiting, and visual disturbances.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
