        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380124, 'Carbamazepine', 'Nilotinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of nilotinib, which is primarily metabolized by the isoenzyme. In healthy subjects receiving the potent inducer rifampin (600 mg once daily for 12 days), nilotinib systemic exposure (AUC) was decreased approximately 80%.', NULL, 'Concomitant use of nilotinib with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, 'Concomitant use of nilotinib with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy. If coadministration is required, a dosage increase for nilotinib may be necessary depending on patient tolerability. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
