        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380133, 'Carbamazepine', 'Olanzapine', 'Moderate', 'The manufacturer reports that carbamazepine (200 mg bid) may increase the clearance of olanzapine by as much as 50%. The mechanism is related to induction of CYP450 activity.', NULL, 'Close clinical monitoring for evidence of altered olanzapine effect is recommended in patients receiving carbamazepine.', NULL, 'Close clinical monitoring for evidence of altered olanzapine effect is recommended in patients receiving carbamazepine. Patients should be advised to report symptoms such as excessive drowsiness, slurred speech, or extrapyramidal symptoms, or agitation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
