        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380138, 'Carbamazepine', 'Ondansetron', 'Moderate', 'Concomitant use of CYP450 3A4 inducers with ondansetron may result in a reduced antiemetic effect, particularly after the oral administration of ondansetron. The proposed mechanism is induction of the CYP450 3A4 mediated metabolism of ondansetron. The clinical significance is unknown.', NULL, 'Patients should be monitored for reduced antiemetic effects and the ondansetron dose adjusted as necessary.', NULL, 'Patients should be monitored for reduced antiemetic effects and the ondansetron dose adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
