        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380179, 'Carbamazepine', 'Pimavanserin', 'Moderate', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration of potent or moderate CYP450 3A4 inducers with pimavanserin should be avoided.', NULL, 'Coadministration of potent or moderate CYP450 3A4 inducers with pimavanserin should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
