        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380182, 'Carbamazepine', 'Pirfenidone', 'Moderate', 'Coadministration with potent inducers of CYP450 1A2 or other isoenzymes involved in the metabolism of pirfenidone such as CYP450 2C9 and 2C19 may significantly decrease the plasma concentrations of pirfenidone. Loss of therapeutic efficacy may result, as pirfenidone is primarily (70% to 80%) metabolized by CYP450 1A2, with minor contribution from CYP450 2C9, 2C19, 2D6, and 2E1.', NULL, 'Potent inducers of CYP450 1A2, 2C9, and/or 2C19 such as phenytoin, carbamazepine, and rifampin should be discontinued prior to the initiation of pirfenidone and avoided during therapy.', NULL, 'Potent inducers of CYP450 1A2, 2C9, and/or 2C19 such as phenytoin, carbamazepine, and rifampin should be discontinued prior to the initiation of pirfenidone and avoided during therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
