        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380207, 'Dextropropoxyphene', 'Carbamazepine', 'Major', 'Coadministration with propoxyphene may significantly increase the plasma concentrations of carbamazepine. The proposed mechanism is propoxyphene inhibition of carbamazepine metabolism via hepatic CYP450 microsomal enzymes.', NULL, 'Given the availability of alternative analgesics, the use of propoxyphene in combination with carbamazepine should generally be avoided.', NULL, 'Given the availability of alternative analgesics, the use of propoxyphene in combination with carbamazepine should generally be avoided. A dose adjustment of carbamazepine may be required if concomitant use is necessary, and serum carbamazepine levels should be closely monitored. Patients should be advised to seek medical attention if they develop potential signs and symptoms of carbamazepine toxicity such as headache, nausea, dizziness, slurred speech, nystagmus, visual disturbances, tremors, and ataxia.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
