INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380213, 'Carbamazepine', 'Quinine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 including carbamazepine may decrease the plasma concentrations of quinine, which is primarily metabolized by the isoenzyme. The extent to which other, less potent inducers of CYP450 3A4 may interact with quinine is unknown. Coadministration with quinine may increase the serum concentrations of carbamazepine. The proposed mechanism is quinine inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of carbamazepine.', NULL, 'The possibility of treatment failure should be considered if quinine must be used in combination with carbamazepine.', NULL, 'The possibility of treatment failure should be considered if quinine must be used in combination with carbamazepine. Patients receiving quinine for malaria should be closely monitored. In addition, serum carbamazepine levels and pharmacologic response should be monitored during and after treatment with quinine, and the carbamazepine dosage adjusted as necessary. Patients should be advised to contact their physician if they experience potential signs and symptoms of carbamazepine toxicity such as headache, nausea, vomiting, dizziness, slurred speech, nystagmus, visual disturbances, tremors, and ataxia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
