        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380215, 'Carbamazepine', 'Raltegravir', 'Minor', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase (UGT) 1A1 may reduce the plasma concentrations of raltegravir, which is primarily metabolized by UGT1A1-mediated glucuronidation. The impact of other potent inducers of drug-metabolizing enzymes such as carbamazepine, phenytoin, and phenobarbital on UGT1A1 is unknown. Other, less potent inducers (e.g., efavirenz, nevirapine, etravirine, rifabutin, dexamethasone, St. John''s Wort, pioglitazone) may be used with the recommended dosage of raltegravir.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
