        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380222, 'Carbamazepine', 'Remdesivir', 'Moderate', 'Theoretical concerns exist that coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of remdesivir, which has been shown in vitro to be a substrate of the metabolic isoenzyme and efflux transporter.', NULL, 'Until more information is available, some experts recommend avoiding the concomitant use of remdesivir with potent CYP450 3A4 or P-gp inducers.', NULL, 'Until more information is available, some experts recommend avoiding the concomitant use of remdesivir with potent CYP450 3A4 or P-gp inducers.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
