        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380225, 'Carbamazepine', 'Ribociclib', 'Major', 'Coadministration with potent CYP450 3A4 inducers may significantly decrease the plasma concentrations and therapeutic effects of ribociclib, which is a substrate of the isoenzyme.', NULL, 'Concomitant use of ribociclib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of ribociclib with potent CYP450 3A4 inducers should generally be avoided. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If coadministration is necessary, dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inducer is added to or withdrawn from therapy. Patients should be monitored for therapeutic failure.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
