    ) VALUES (1380255, 'Carbamazepine', 'Selpercatinib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of selpercatinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of selpercatinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
