        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380284, 'Carbamazepine', 'Tacrolimus', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein may significantly decrease the plasma concentrations and pharmacologic effects of tacrolimus. The mechanism probably involves reduced absorption as well as accelerated clearance of tacrolimus due to induction of both intestinal P-glycoprotein drug efflux transporter and hepatic/intestinal CYP450 3A4 isoenzymes.', NULL, 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, caution is advised if tacrolimus must be coadministered with potent inducers of CYP450 3A4 and/or P-gp.', NULL, 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, caution is advised if tacrolimus must be coadministered with potent inducers of CYP450 3A4 and/or P-gp. Tacrolimus blood levels should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of inducer therapy in patients who are stabilized on their anti-rejection regimen.', 'Metabolism, Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
