    ) VALUES (1380291, 'Carbamazepine', 'Telithromycin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of telithromycin, which is primarily metabolized by the isoenzyme. Enzyme activities usually return to normal 14 days after discontinuation of the inducing agent.', NULL, 'Telithromycin should not be used during or within 2 weeks after discontinuation of treatment with a potent CYP450 3A4 inducer.', NULL, 'Telithromycin should not be used during or within 2 weeks after discontinuation of treatment with a potent CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
