        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380294, 'Carbamazepine', 'Temsirolimus', 'Major', 'Coadministration of temsirolimus with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inducers should generally be avoided. If coadministration is required in patients treated with temsirolimus for renal cell carcinoma, the manufacturer recommends increasing the temsirolimus dosage from 25 mg to 50 mg once a week depending on patient tolerability. Based on pharmacokinetic studies, this dosage is predicted to adjust the sirolimus systemic exposure (AUC) to the range observed without inducers. However, clinical data are lacking. The dosage should be reduced to the normally recommended dosage (i.e., 25 mg once a week) following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
