    ) VALUES (1380295, 'Carbamazepine', 'Teniposide', 'Moderate', 'Some anticonvulsants such as carbamazepine, phenobarbital, phenytoin, and primidone may reduce the systemic exposure of teniposide. The proposed mechanism is accelerated clearance of teniposide due to induction of hepatic CYP450 enzymes by these anticonvulsants.', NULL, 'During concomitant therapy with certain anticonvulsants, patients should be observed for potentially inadequate or altered response to teniposide, and the dosage adjusted accordingly as necessary.', NULL, 'During concomitant therapy with certain anticonvulsants, patients should be observed for potentially inadequate or altered response to teniposide, and the dosage adjusted accordingly as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
