    ) VALUES (1380306, 'Carbamazepine', 'Thiothixene', 'Moderate', 'Carbamazepine may reduce plasma levels and effects of thiothixene. The mechanism may be related to induction of the CYP450 hepatic metabolism.', NULL, 'Monitoring for loss of antipsychotic control is recommended if these drugs are coadministered.', NULL, 'Monitoring for loss of antipsychotic control is recommended if these drugs are coadministered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
