        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380308, 'Carbamazepine', 'Tiagabine', 'Moderate', 'Population pharmacokinetic studies indicate that tiagabine clearance is 60% greater in patients receiving concomitant carbamazepine with or without other enzyme inducing antiepileptic drugs (AEDs). The mechanism is thought to be induction of the CYP450 3A4 metabolism of tiagabine by carbamazepine.', NULL, 'Clinical and laboratory monitoring may be necessary if these medications are used together, especially if carbamazepine is started or stopped in a patient stabilized on tiagabine.', NULL, 'Clinical and laboratory monitoring may be necessary if these medications are used together, especially if carbamazepine is started or stopped in a patient stabilized on tiagabine. Patients should be advised to notify their physician if they experience loss of seizure control.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
