    ) VALUES (1380319, 'Carbamazepine', 'Trabectedin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', NULL, 'Given the potential for diminished pharmacologic effects of trabectedin in the presence of potent CYP450 3A4 inducers, concomitant use should generally be avoided.', NULL, 'Given the potential for diminished pharmacologic effects of trabectedin in the presence of potent CYP450 3A4 inducers, concomitant use should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
