    ) VALUES (1380339, 'Carbamazepine', 'Tubocurarine', 'Moderate', 'Carbamazepine may reduce the effects or duration of nondepolarizing neuromuscular blocking agents. The mechanism is unknown.', NULL, 'Close observation for decreased neuromuscular blocking effect is recommended if a patient on carbamazepine must receive a nondepolarizing neuromuscular blocking agent.', NULL, 'Close observation for decreased neuromuscular blocking effect is recommended if a patient on carbamazepine must receive a nondepolarizing neuromuscular blocking agent.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
