    ) VALUES (1380341, 'Carbamazepine', 'Ubrogepant', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme. When ubrogepant was administered with the potent CYP450 3A4 inducer rifampin during in vivo studies, ubrogepant systemic exposure (AUC) decreased by 80%. Reduced efficacy of ubrogepant may occur.', NULL, 'Concomitant use of ubrogepant with potent CYP450 3A4 inducers should be avoided.', NULL, 'Concomitant use of ubrogepant with potent CYP450 3A4 inducers should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
