        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380345, 'Carbamazepine', 'Valbenazine', 'Major', 'Coadministration of valbenazine with strong CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of valbenazine and its active metabolite, which are substrates of the isoenzyme. The proposed mechanism is induction of CYP450 3A4-mediated metabolism of valbenazine.', NULL, 'The use of valbenazine with strong CYP450 3A4 inducers should generally be avoided.', NULL, 'The use of valbenazine with strong CYP450 3A4 inducers should generally be avoided. If concomitant use is required, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may be required whenever a strong CYP450 3A4 inducer is added to or withdrawn from therapy. Alternative treatment may be considered if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
