INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94203, 'Tamsulosin', 'Prazosin', 'Moderate', 'Additive pharmacodynamic effects may be expected when relatively selective alpha-1A adrenoreceptor antagonists are coadministered with other alpha-blockers. Clinical data have not been reported, but there may be an increased risk and/or severity of adverse effects related to blockade of alpha-1 adrenergic receptors in the peripheral vasculature such as postural hypotension, dizziness, headache, syncope, priapism, and nasal congestion. These effects have occurred with alpha-1A adrenoreceptor antagonists even when given alone for the treatment of benign prostatic hyperplasia, despite relative selectivity for the 1A subtype of alpha-1 adrenoreceptors located in prostatic and bladder tissues.', NULL, 'The use of relatively selective alpha-1A adrenoreceptor antagonists in combination with other alpha-blockers should be avoided.', NULL, 'The use of relatively selective alpha-1A adrenoreceptor antagonists in combination with other alpha-blockers should be avoided.', 'Synergism', 'Rauwolfia serpentina root, Reserpine, Guanethidine, Deserpidine, Guanethidine', 'Finasteride, Dutasteride, Terazosin, Solifenacin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94307/', '[1] "Product Information. Flomax (tamsulosin)." Boehringer-Ingelheim, Ridgefield, CT.[2] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.[3] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.', 1767369485);
