INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94250, 'Tizanidine', 'Tamsulosin', 'Major', 'Tizanidine may potentiate the hypotensive effect of some medications secondary to its alpha-2 adrenergic activity. Pharmacologic studies have found tizanidine to possess between 1/10 to 1/50 of the potency of clonidine, a structurally similar agent, in lowering blood pressure.', NULL, 'A lower initial dosage and cautious dosage titration should be considered when tizanidine is initiated in patients receiving hypotensive medications.', NULL, 'A lower initial dosage and cautious dosage titration should be considered when tizanidine is initiated in patients receiving hypotensive medications. Although single doses of less than 8 mg of tizanidine have not been shown effective for spasticity in controlled clinical studies, it may be prudent to initiate treatment with 4 mg doses and gradually increase in 2 to 4 mg increments until optimum effect is achieved. The dose can be repeated at 6 to 8 hour intervals as needed, up to a maximum of three doses in 24 hours and a total daily dosage of 36 mg. However, experience with single doses exceeding 8 mg and daily doses exceeding 24 mg is limited. Close monitoring for development of hypotension is recommended.', 'Synergism', 'Silodosin, Dutasteride, Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Ciclopirox, Darunavir, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94354/', '[1] "Product Information. Zanaflex (tizanidine)." Acorda Therapeutics, Hawthorne, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94251, 'Tolazoline', 'Tamsulosin', 'Moderate', 'Additive pharmacodynamic effects may be expected when relatively selective alpha-1A adrenoreceptor antagonists are coadministered with other alpha-blockers. Clinical data have not been reported, but there may be an increased risk and/or severity of adverse effects related to blockade of alpha-1 adrenergic receptors in the peripheral vasculature such as postural hypotension, dizziness, headache, syncope, priapism, and nasal congestion. These effects have occurred with alpha-1A adrenoreceptor antagonists even when given alone for the treatment of benign prostatic hyperplasia, despite relative selectivity for the 1A subtype of alpha-1 adrenoreceptors located in prostatic and bladder tissues.', NULL, 'The use of relatively selective alpha-1A adrenoreceptor antagonists in combination with other alpha-blockers should be avoided.', NULL, 'The use of relatively selective alpha-1A adrenoreceptor antagonists in combination with other alpha-blockers should be avoided.', 'Synergism', 'Finasteride, Dutasteride, Terazosin, Solifenacin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, More', 'Phenylbutazone, Ketoprofen, Flurbiprofen, Tolmetin, Meclofenamic acid, Dimethyl sulfoxide, Cyclandelate, Isoxsuprine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94355/', '[1] "Product Information. Flomax (tamsulosin)." Boehringer-Ingelheim, Ridgefield, CT.[2] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.[3] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.', 1767369485