INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94263, 'Verteporfin', 'Tamsulosin', 'Moderate', 'Drugs that decrease clotting, vasoconstriction, or platelet aggregation may interfere with the therapeutic effect of verteporfin. These agents may antagonize the physiologic responses to local damage of neovascular endothelium induced by activated verteporfin, which include the release of procoagulant and vasoactive factors that result in vessel occlusion. However, the clinical significance is unknown.', NULL, 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy.', NULL, 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy. Otherwise, the potential for diminished efficacy of verteporfin should be considered.', 'Antagonism', 'Aflibercept, Bevacizumab', 'Dutasteride, Solifenacin, Tinidazole, Ascorbic acid, Metronidazole, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Ciclopirox, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94367/', '[1] "Product Information. Visudyne (verteporfin)" Valeant Pharmaceuticals, Costa Mesa, CA.[2] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94264, 'Voriconazole', 'Tamsulosin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tamsulosin, which is primarily metabolized by the hepatic microsomal isoenzymes CYP450 3A4 and 2D6. Severe hypotension and priapism may occur.', NULL, 'Since CYP450 2D6 poor metabolizers cannot be readily identified, concomitant use of tamsulosin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Since CYP450 2D6 poor metabolizers cannot be readily identified, concomitant use of tamsulosin with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of tamsulosin during and for 2 weeks after treatment with itraconazole. If tamsulosin administration is discontinued for several days or more at either the 0.4 or 0.8 mg dose, therapy should be reinitiated with the 0.4 mg once-daily dose and titrated gradually as needed.', 'Metabolism', 'Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 'Terazosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Trichlormethiazide, Ciclopirox, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94368/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] "Product Information. Flomax (tamsulosin)." Boehringer-Ingelheim, Ridgefield, CT.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Franco-Salinas G, de la Rosette JJ, Michel MC "Pharmacokinetics and pharmacodynamics of tamsulosin in its modified-release and 