INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94480, 'Duvelisib', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Avapritinib, Selumetinib, Vandetanib, Ibrutinib, Tivozanib, More', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94584/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
