INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94533, 'Duvelisib', 'Tadalafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tadalafil should be considered.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors. Dosage adjustments may be appropriate for tadalafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects. Tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours in patients treated concomitantly with a potent CYP450 3A4 inhibitor, such as erythromycin, itraconazole, ketoconazole, protease inhibitors, and nefazodone. Patients should be advised to promptly notify their physician if they experience potential symptoms of PDE5 inhibitor toxicity such as pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94637/', '[1] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94534, 'Duvelisib', 'Talimogene laherparepvec', 'Major', 'Talimogene laherparepvec is a live, attenuated herpes simplex virus. Administration during immunosuppressant or intense antineoplastic therapy may be associated with a risk of potentially life-threatening disseminated herpetic infection due to enhanced virus replication in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Treatment with talimogene laherparepvec may need to be deferred until after such therapy is discontinued and the patient''s immune system has sufficiently recovered.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94638/', '[1] "Product Information. Imlygic (talimogene laherparepvec)." Amgen USA, Thousand Oaks, CA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94535, 'Duvelisib', 'Tamoxifen', 'Moderate', 'Coadministration with duvelisib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by duvelisib. The interaction may be particularly important for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Caution is advised if duvelisib is used in combination with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, 'Caution is advised if duvelisib is used in combination with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vinca alkaloids).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94639/', '[1] "Product Information. Copiktra (duvelisib)." Verastem, Inc., Needham, MA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94536, 'Duvelisib', 'Tasimelteon', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', NULL, 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4.', NULL, 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4. Patients should be monitored for excessive sedation and other side effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94640/', '[1] "Product Information. Hetlioz (tasimelteon)." Vanda Pharmaceuticals Inc, Rockville, MD.[2] "Product Information. Hetlioz (tasimelteon)." Vanda Pharmaceuticals Inc, Rockville, MD.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94537, 'Duvelisib', 'Tazemetostat', 'Major', 'Coadministration with moderate or potent CYP450 3A4 inhibitors may significantly increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', NULL, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided. Alternative therapeutic agents with less inhibitory potential should be considered whenever possible. If coadministration with a moderate CYP450 3A4 inhibitor is required and no other alternatives are possible, the dose of tazemetostat should be reduced as per manufacturer recommendations. After the inhibitor has been discontinued for 3 elimination half-lives, the tazemetostat dose that was taken prior to initiating the inhibitor may be resumed. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94641/', '[1] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.[2] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.', 1767365205);
