INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94669, 'Minocycline', 'Methotrexate', 'Moderate', 'Concomitant use of tetracyclines may elevate or reduce serum methotrexate concentrations. One mechanism may involve displacement of methotrexate from its binding to plasma proteins. Individual cases of methotrexate toxicity have been reported with doxycycline and tetracycline. In contrast, it has also been reported that oral antibiotics such as tetracyclines may reduce the intestinal absorption of methotrexate or interfere with its enterohepatic circulation or by inhibiting bowel flora and suppressing the metabolism of methotrexate by bacteria. However, the clinical significance of this effect has not been established.', NULL, 'If these drugs must be used together, close monitoring for evidence of serious methotrexate toxicity or reduced therapeutic efficacy is recommended.', NULL, 'If these drugs must be used together, close monitoring for evidence of serious methotrexate toxicity or reduced therapeutic efficacy is recommended. If broad-spectrum antibiotic coverage is needed during high-dose methotrexate therapy, the use of an alternate anti-infective may be considered.', 'Absorption, Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94773/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Tortajada-Ituren JJ, Ordovas-Baines JP, Llopis-Salvia P, Jimenez-Torres NV "High-dose methotrexate-doxycycline interaction." Ann Pharmacother 33 (1999): 804-8[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Methotrexate (methotrexate)." Lederle Laboratories, Wayne, NJ.[6] Nesher G, Mates M, Zevin S "Effect of caffeine consumption on efficacy of methotrexate in rheumatoid arthritis." Arthritis Rheum 48 (2003): 571-572[7] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[8] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94670, 'Minocycline', 'Methoxsalen', 'Moderate', 'Concomitant use of methoxsalen with other known photosensitizing agents may increase the risk of photosensitivity reactions. These agents include anthralin, coal tar or coal tar derivatives, griseofulvin, hypericin extracts (e.g., St John''s Wort), fluoroquinolones, phenothiazines, retinoids, halogenated salicylanilides (bacteriostatic soaps), sulfonamides, sulfonylureas, tetracyclines, thiazides, and certain organic staining dyes such as methylene blue, toluidine blue, rose bengal, and methyl orange.', NULL, 'Caution is advised and pharmacologic response to methoxsalen therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, 'Caution is advised and pharmacologic response to methoxsalen therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid sun exposure, even through window glass or cloud cover, for at least 8 hours after methoxsalen ingestion and during the 24 hours following photochemotherapy or photopheresis treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94774/', '[1] "Product Information. Uvadex (methoxsalen)." Therakos Inc, Exton, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Multum Information Services, Inc. Expert Review Panel"[4] "Product Information. Oxsoralen (methoxsalen)." ICN Pharmaceuticals Inc, Costa Mesa, CA.[5] Royal Australian College of General Practicioners (RACGP), the Pharmaceutical Society of Australia (PSA), the Australasian Society of Clinical and Experimental Pharmacologists and Toxicologists (ASCEPT) "Australian Medicines Handbook. Available from: URL: https://www.amh.net.au/"[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365205);
