INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94704, 'Sucralfate', 'Minocycline', 'Moderate', 'INTERVAL: Simultaneous administration of sucralfate may interfere with the absorption of tetracyclines and reduce their oral bioavailability. Sucralfate is an aluminum salt of a sulfated disaccharide. The aluminum moiety can dissociate at gastric pH and chelate with tetracyclines to form an insoluble complex that is poorly absorbed from the gastrointestinal tract. Reduced antibiotic efficacy may occur.', NULL, 'To minimize the potential for interaction, tetracyclines should be administered at least 2 hours before sucralfate.', NULL, 'To minimize the potential for interaction, tetracyclines should be administered at least 2 hours before sucralfate.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94808/', '[1] "Product Information. Carafate (sucralfate)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[3] McCarthy DM "Sucralfate." N Engl J Med 325 (1991): 1017-25[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94705, 'Iron sucrose', 'Minocycline', 'Moderate', 'INTERVAL: Coadministration with sucroferric oxyhydroxide may reduce the oral bioavailability of tetracyclines. The proposed mechanism is chelation of tetracyclines to the ferric ion component of sucroferric oxyhydroxide, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Doxycycline should be administered at least 1 hour before sucroferric oxyhydroxide.', NULL, 'Doxycycline should be administered at least 1 hour before sucroferric oxyhydroxide. Omadacycline administration with sucroferric oxyhydroxide should be separated by 4 hours. No dosing recommendations are available for use with other tetracyclines, although patients should consider following the same precaution.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94809/', '[1] Neuvonen PJ "Interactions with the absorption of tetracyclines." Drugs 11 (1976): 45-54[2] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[3] "Product Information. Velphoro (sucroferric oxyhydroxide)." Fresenius Medical Care North America, Lexington, MA.[4] Campbell NR, Hasinoff BB "Iron supplements: a common cause of drug interactions." Br J Clin Pharmacol 31 (1991): 251-5[5] Greenberger NJ "Absorption of tetracyclines: interference by iron." Ann Intern Med 74 (1971): 792-3[6] Neuvonen PJ, Gothoni G, Hackman R, Bjorksten K "Interference of iron with the absorption of tetracyclines in man." Br Med J 4 (1970): 532-4[7] Gothoni G, Neuvonen PJ, Mattila M, Hackman R "Iron-tetracycline interaction: effect of time interval between the drugs." Acta Med Scand 191 (1972): 409-11[8] Venho VM, Salonen RO, Mattila MJ "Modification of the pharmacokinetics of doxycycline in man by ferrous sulphate or charcoal." Eur J Clin Pharmacol 14 (1978): 277-80[9] Neuvonen PJ, Penttila O "Effect of oral ferrous sulphate on the half-life of doxycycline in man." Eur J Clin Pharmacol 7 (1974): 361-3[10] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[11] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[12] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94706, 'Teriflunomide', 'Minocycline', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', NULL, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', NULL, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94810/', '[1] EMEA "EMEA public statement on leflunomide (ARAVA) - severe and serious hepatic reactions. Available from URL: http://www.eudra.org/emea.html." ([1999 Sept 2]):[2] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94707, 'Theophylline', 'Minocycline', 'Moderate', 'Coadministration of a tetracycline may decrease theophylline plasma clearance and increase theophylline levels. Data are available for minocycline, tetracycline, and doxycycline, but are conflicting in some cases. This interaction appears to be more problematic if a tetracycline is administered for more than five days. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity.', NULL, 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended.', NULL, 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended. Patients should be advised to notify their physician if they experience any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats. It may be necessary to reduce theophylline dosage.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94811/', '[1] McCormack JP, Reid SE, Lawson LM "Theophylline toxicity induced by tetracycline." Clin Pharm 9 (1990): 546-9[2] Gotz VP, Ryerson GG "Evaluation of tetracycline on theophylline disposition in patients with chronic obstructive airways disease." Drug Intell Clin Pharm 20 (1986): 694-6[3] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part I)." Clin Pharmacokinet 20 (1991): 66-80[4] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[5] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94708, 'Tioguanine', 'Minocycline', 'Moderate', 'The concomitant or sequential use of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with thioguanine. A high risk of liver toxicity characterized by vascular endothelial damage has been reported with long-term continuous use of thioguanine, particularly in children receiving the drug as part of maintenance therapy for acute lymphoblastic leukemia and in other conditions associated with continuous use.', NULL, 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Baseline and regular monitoring of hepatic function is recommended. Thioguanine therapy should be discontinued if there is evidence of toxic hepatitis or biliary stasis, as reversal of signs and symptoms of liver toxicity have been reported upon withdrawal. Early indications of liver toxicity are signs associated with portal hypertension such as thrombocytopenia out of proportion with neutropenia and splenomegaly. Elevations of liver enzymes have also been reported, but do not always occur.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94812/', '[1] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Tabloid (thioguanine)." Prasco Laboratories, Cincinnati, OH.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (94709, 'Ticarcillin', 'Minocycline', 'Moderate', 'Tetracyclines may reduce the effect of penicillins by inhibiting cellular protein synthesis which is necessary for cell wall synthesis inhibition by penicillins. Antagonism is more likely when low doses of either agent are administered. Therapeutic failure may result.', NULL, 'This combination should be avoided if possible.', NULL, 'This combination should be avoided if possible.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/94813/', '[1] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[2] Olsson RA, Kirby JC, Romansky MJ "Pneumococcal meningitis in the adult." Ann Intern Med 55 (1961): 545-9[3] Jawetz E "Synergism and antagonism among antimicrobial drugs: a personal perspective." West J Med 123 (1975): 87-91[4] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.[5] Lepper MH, Dowling HF "Treatment of pneumococcic meningitis with penicillin compared with penicillin plus aureomycin." Arch Intern Med 88 (1951): 489-94[6] Gunnison JB, Coleman VR, Jawetz E "Interference of aureomycin and of terramycin with action of penicillin in vitro." Proc Soc Exp Biol Med 75 (1950): 549-52[7] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[8] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365205);
