        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1380589, 'Tetrabenazine', 'Levodopa', 'Moderate', 'Tetrabenazine can cause or exacerbate Parkinsonian symptoms and attenuate the pharmacologic effects of levodopa. The mechanism is reversible binding of human vesicular monoamine transporter type 2 (VMAT2) by tetrabenazine, which leads to depletion of monoamine in the central nervous system including dopamine.', NULL, 'Concomitant use of tetrabenazine and levodopa should be avoided if possible.', NULL, 'Concomitant use of tetrabenazine and levodopa should be avoided if possible. Tetrabenazine is not indicated for the treatment of levodopa-induced dyskinetic or choreiform movements, which should instead be treated by reducing the dosage of levodopa.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
