    ) VALUES (1380981, 'Levothyroxine', 'Levonordefrin', 'Moderate', 'The concomitant use of thyroid hormones, especially large doses, with sympathomimetic drugs may potentiate sympathomimetic effects and increase the risk of cardiac insufficiency in patients who have coronary artery disease.', NULL, 'Monitoring for increased sympathomimetic effects and cardiac function may be advisable.', NULL, 'Monitoring for increased sympathomimetic effects and cardiac function may be advisable.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
