INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95002, 'Halothane', 'Dyphylline', 'Major', 'The administration of halothane following theophylline administration has been reported to result in ventricular tachycardia and, in one case, ventricular fibrillation. Halothane increases the arrhythmogenic potential of catecholamines. Theophylline appears to enhance the release of catecholamines.', NULL, 'This combination should be avoided if possible.', NULL, 'This combination should be avoided if possible. Halothane may interact in a similar manner with other methylxanthines.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95106/', '[1] Roizen MF, Stevens WC "Multiform ventricular tachycardia due to the interaction of aminophylline and halothane." Anesth Analg 57 (1978): 738-41[2] Richards W, Thompson J, Lewis G, et al "Cardiac arrest associated with halothane anesthesia in a patient receiving theophylline." Ann Allergy 61 (1988): 83-4', 1767365206);
