INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95028, 'Dyphylline', 'Probenecid', 'Minor', 'Probenecid may inhibit the renal elimination of dyphylline. Dyphylline concentrations and risk of toxicity may be significantly increased. The clinical significance is unknown, as data are based on single-dose studies. Theophylline pharmacokinetics do not appear to be affected by probenecid and may be considered as an alternative.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95132/', '[1] May DC, Jarboe CH "Effect of probenecid on dyphylline elimination." Clin Pharmacol Ther 33 (1983): 822-5[2] Acara M, Carr EA Jr, Terry EN "Probenecid inhibition of the renal excretion of dyphyllin in chicken, rat and man." J Pharm Pharmacol 39 (1987): 526-30[3] Nadai M, Apichartpichean R, Hasegawa T, Nabeshima T "Pharmacokinetics and the effect of probenecid on the renal excretion mechanism of diprophylline." J Pharm Sci 81 (1992): 1024-7', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95029, 'Dyphylline', 'Propranolol', 'Major', 'The pharmacologic effects of theophyllines and beta-blockers are opposite. Nonselective and high doses of cardioselective beta-blockers may cause severe or fatal bronchospasm by opposing theophylline-induced bronchodilation. Ophthalmic beta-blockers undergo significant systemic absorption and may also interact. In addition, propranolol and other beta-blockers may reduce the CYP450 hepatic metabolism of theophylline, and serum theophylline levels may be increased.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.g., carteolol, carvedilol, levobunolol, metipranolol, nadolol, oxprenolol, penbutolol, pindolol, propranolol, sotalol, and timolol) are considered contraindicated in patients with bronchospastic diseases. Cardioselective beta-blockers should generally be avoided, or used with extreme caution if no other alternatives are available and the benefits outweigh the risks of potentially severe bronchospasm. If patients do receive this combination, they should be closely monitored for increased serum theophylline levels but decreased bronchodilatory effectiveness.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95133/', '[1] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part II)." Clin Pharmacokinet 20 (1991): 135-50[2] Mue S, Sasaki T, Shibahara S, et alT "Influence of metoprolol on hemodynamics and respiratory function in asthmatic patients." Int J Clin Pharmacol Biopharm 17 (1979): 346-50[3] Lombardi TP, Bertino JS, Goldberg A, Middleton E, Slaughter RL "The effects of a beta-2 selective adrenergic agonist and a beta- nonselective antagonist on theophylline clearance." J Clin Pharmacol 27 (1987): 523-9[4] Horvath JS, Woolcock AJ, Tiller DJ, Donnelly P, Armstrong J, Caterson R "A comparison of metoprolol and propranolol on blood pressure and respiratory function in patients with hypertension." Aust N Z J Med 8 (1978): 1-6[5] Conrad KA, Nyman DW "Effects of metoprolol and propranolol on theophylline elimination." Clin Pharmacol Ther 28 (1980): 463-7', 1767365206);
