INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95404, 'Erythromycin', 'Eplerenone', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of eplerenone, which is primarily metabolized by the isoenzyme.', NULL, 'When used with moderate CYP450 3A4 inhibitors, the initial dosage of eplerenone should be reduced to 25 mg once daily for the treatment of hypertension.', NULL, 'When used with moderate CYP450 3A4 inhibitors, the initial dosage of eplerenone should be reduced to 25 mg once daily for the treatment of hypertension. Patients already on eplerenone who start treatment with a moderate CYP450 3A4 inhibitor should have serum potassium and creatinine checked in 3 to 7 days.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95508/', '[1] "Product Information. Inspra (eplerenone)." Searle, Chicago, IL.', 1767365206);
