INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95430, 'Flibanserin', 'Erythromycin', 'Major', 'Coadministration with moderate and potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19. High plasma levels of flibanserin may increase the risk of severe hypotension, syncope, and central nervous system depression.', NULL, 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated. When initiating flibanserin following treatment with a moderate or potent CYP450 3A4 inhibitor, the manufacturer recommends waiting until 2 weeks after the last dose of CYP450 3A4 inhibitor. Conversely, if a moderate or potent CYP450 3A4 inhibitor is required during flibanserin therapy, flibanserin should be discontinued at least 2 days before starting the CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95534/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[2] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95431, 'Fluconazole', 'Erythromycin', 'Major', 'Coadministration with fluconazole may increase the plasma concentrations of erythromycin. The proposed mechanism is fluconazole inhibition of the CYP450 3A4-mediated metabolism of erythromycin. High dosages and plasma levels of erythromycin have been associated with QT interval prolongation and torsade de pointes arrhythmia. Fluconazole itself can also prolong the QT interval, thus an additive pharmacodynamic effect with erythromycin may occur as well.', NULL, 'Coadministration of erythromycin with fluconazole is considered contraindicated.', NULL, 'Coadministration of erythromycin with fluconazole is considered contraindicated.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95535/', '[1] "Product Information. Diflucan (fluconazole)." Roerig Division, New York, NY.[2] Ray WA, Murray KT, Meredith S, Narasimhulu SS, Hall K, Stein CM "Oral erythromycin and the risk of sudden death from cardiac causes." N Engl J Med 351 (2004): 1089-96[3] Iannini PB "Cardiotoxicity of macrolides, ketolides and fluoroquinolones that prolong the QTc interval." Expert Opin Drug Saf 1 (2002): 121-8[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Welling PG, Huang H, Hewitt PF, Lyons LL "Bioavailability of erythromycin stearate: influence of food and fluid volume." J Pharm Sci 67 (1978): 764-6[6] Welling PG "Influence of food and diet on gastrointestinal drug absorption: a review." J Pharmacokinet Biopharm 5 (1977): 291-334[7] Randinitis EJ, Sedman AJ, Welling PG, Kinkel AW "Effect of a high-fat meal on the bioavailability of a pol