INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95435, 'Fluoxetine', 'Erythromycin', 'Moderate', 'The use of some macrolide antibiotics has been associated with increased blood levels of some selective serotonin reuptake inhibitors (SSRIs), resulting in excessive serotonergic effects or serotonin syndrome. The mechanism is inhibition of the CYP450 3A4 isoenzyme, which metabolizes SSRIs.', NULL, 'In cases where both drugs are necessary, one could consider using a macrolide antibiotic that does not inhibit CYP450 3A4, such as azithromycin or dirithromycin.', NULL, 'In cases where both drugs are necessary, one could consider using a macrolide antibiotic that does not inhibit CYP450 3A4, such as azithromycin or dirithromycin.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95539/', '[1] Martin TG "Serotonin syndrome." Ann Emerg Med 28 (1996): 520-6[2] Pollak PT, Slayter KL "Delirium probably induced by clarithromycin in a patient receiving fluoxetine - reply." Ann Pharmacother 30 (1996): 1200[3] Timothy P, Sketris IS, Mackenzie SL, Hewlett TJ "Delirium probably induced by clarithromycin in a patient receiving fluoxetine." Ann Pharmacother 29 (1995): 486-8[4] Tracy TS, Cupp MJ "Delirium probably induced by clarithromycin in a patient receiving fluoxetine - comment." Ann Pharmacother 30 (1996): 1199-200[5] Lee DO, Lee CD "Serotonin syndrome in a child associated with erythromycin and sertraline." Pharmacotherapy 19 (1999): 894-6[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Ueda N, Yoshimura R, Umene-Nakano W, et al. "Grapefruit juice alters plasma sertraline levels after single ingestion of sertraline in healthy