INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95438, 'Flutamide', 'Erythromycin', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95542/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95439, 'Fluticasone', 'Erythromycin', 'Minor', 'Coadministration with erythromycin, a moderately potent CYP450 3A4 inhibitor, does not appear to significantly alter the systemic exposure to fluticasone following intranasal administration or oral inhalation. Fluticasone undergoes extensive first-pass and systemic metabolism via hepatic and intestinal CYP450 3A4, and inhibition of the isoenzyme by potent inhibitors such as ritonavir and ketoconazole has been shown to increase systemic bioavailability of the drug. However, a clinically significant interaction with erythromycin has not been reported.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95543/', '[1] "Product Information. Veramyst (fluticasone nasal)." GlaxoSmithKline, Research Triangle Park, NC.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Flovent (fluticasone)." Glaxo Wellcome, Research Triangle Park, NC.[4] "Product Information. Flonase (fluticasone)." Glaxo Wellcome, Research Triangle Park, NC.[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Welling PG, Huang H, Hewitt PF, Lyons LL "Bioavailability of erythromycin stearate: influence of food and fluid volume." J Pharm Sci 67 (1978): 764-6[7] Welling PG "Influence of food and diet on gastrointestinal drug absorption: a review." J Pharmacokinet Biopharm 5 (1977): 291-334[8] Randinitis EJ, Sedman AJ, Welling PG, Kinkel AW "Effect of a high-fat meal on the bioavailability of a polymer-coated erythromycin particle t