INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95440, 'Fluvastatin', 'Erythromycin', 'Major', 'Severe myopathy and rhabdomyolysis have been reported with concomitant use of HMG-CoA reductase inhibitors and erythromycin in severely ill patients. The suspected mechanism is inhibition of HMG-CoA reductase inhibitor metabolism by CYP450 3A4 isoenzymes.', NULL, 'While these effects have not been reported with concomitant use of fluvastatin and erythromycin, alternative therapy should be considered.', NULL, 'While these effects have not been reported with concomitant use of fluvastatin and erythromycin, alternative therapy should be considered. If patients do receive this combination, they should be instructed to report symptoms of muscular pain, weakness, tenderness, malaise, or fever. If such symptoms occur, creatine kinase should be measured. Fluvastatin and erythromycin should be discontinued if creatine kinase is elevated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95544/', '[1] Spach DH, Bauwens JE, Clark CD, Burke WG "Rhabdomyolysis associated with lovastatin and erythromycin use." West J Med 154 (1991): 213-5[2] "Product Information. Lescol (fluvastatin)." Novartis Pharmaceuticals, East Hanover, NJ.[3] Welling PG, Huang H, Hewitt PF, Lyons LL "Bioavailability of erythromycin stearate: influence of food and fluid volume." J Pharm Sci 67 (1978): 764-6[4] Welling PG "Influence of food and diet on gastrointestinal drug absorption: a review." J Pharmacokinet Biopharm 5 (1977): 291-334[5] Randinitis EJ, Sedman AJ, Welling PG, Kinkel AW "Effect of a high-fat meal on the bioavailability of a polymer-coated erythromycin particle tablet