INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95450, 'Gefitinib', 'Erythromycin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of gefitinib, which is primarily metabolized by the isoenzyme. According to the product labeling, administration of gefitinib (250 mg single dose) with the potent inhibitor itraconazole (200 mg once a day for 12 days) increased the mean gefitinib systemic exposure (AUC) by 88% in healthy male volunteers.', NULL, 'Caution is advised if gefitinib is administered with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', NULL, 'Caution is advised if gefitinib is administered with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors. Pharmacologic response to gefitinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience possible symptoms of gefitinib toxicity such as severe diarrhea, nausea, dyspnea, cough, and fever.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95554/', '[1] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007): 3731-7[2] "Product Information. Iressa (gefitinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (95451, 'Gemifloxacin', 'Erythromycin', 'Moderate', 'Certain quinolones, including gemifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation may increase with increasing plasma concentrations of gemifloxacin, the recommended dosage should not be exceeded, especially in patients with renal or hepatic impairment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95555/', '[1] Iannini PB, Doddamani S, Byazrova E, Curciumaru I, Kramer H "Risk of torsades de pointes with non-cardiac drugs." BMJ 322 (2001): 46-7[2] Ball P "Quinolone-induced QT interval prolongation: a not-so-unexpected class effect." J Antimicrob Chemother 45 (2000): 557-9[3] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[4] Owens RC "Risk assessment for antimicrobial agent-induced QTc interval prolongation and torsades de pointes." Pharmacotherapy 21 (2001): 301-19[5] Kang J, Wang L, Chen XL, Triggle DJ, Rampe D "Interactions of a series of fluoroquinolone antibacterial drugs with the human cardiac K+ channel HERG." Mol Pharmacol 59 (2001): 122-6', 1767365206);
