INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380997, 'Methylergometrine', 'Levonordefrin', 'Major', 'Additive or synergistic increases in blood pressure and/or ischemic response may occur when ergot alkaloids are combined with peripheral or central vasoconstrictors. Ergot alkaloids produce arterial vasoconstriction by stimulating alpha-adrenergic and serotonin receptors and inhibiting endothelial-derived relaxation factor release.', 'DDInter', 1767317475, 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents. If concomitant use is required, patients should be monitored for excessive vasoconstriction and have vital signs measured regularly. Patients should also be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380998, 'Methylphenidate', 'Levonordefrin', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, can potentiate the adrenergic response to vasopressors and other sympathomimetic agents. Additive increases in blood pressure and heart rate may occur due to enhanced peripheral sympathetic activity.', 'DDInter', 1767317475, 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents. Pulse and blood pressure should be closely monitored.', 'Synergism', 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
