INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381228, 'Carboplatin', 'Sorafenib', 'Major', 'Coadministration of sorafenib in combination with carboplatin and paclitaxel has been associated with an increased risk of death in patients with squamous cell lung cancer. The exact cause has not been identified, and no significant pharmacokinetic changes in any of the three drugs were observed in two phase I clinical studies.', 'DDInter', 1767317475, 'The concomitant use of sorafenib with carboplatin and paclitaxel is contraindicated in patients with squamous cell lung cancer. Caution may be advisable when this combination is used in other malignancies.', 'Others', 'The concomitant use of sorafenib with carboplatin and paclitaxel is contraindicated in patients with squamous cell lung cancer.', NULL, NULL, 'Major', 'pharmacodynamic');
