INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381428, 'Fenoldopam', 'Diltiazem', 'Moderate', 'The use of fenoldopam with other hypotensive agents has not been studied. However, these combinations would theoretically increase the risk of severe symptomatic hypotension.', 'DDInter', 1767317475, 'Caution is strongly advised during concurrent use. Inpatient monitoring of the heart rate and blood pressure is recommended. Sublingual nitroglycerin has been used safely with fenoldopam.', 'Synergism', 'Caution is strongly advised during concurrent use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381429, 'Fenoprofen', 'Diltiazem', 'Moderate', 'Limited data indicate that some cyclooxygenase inhibitors may attenuate the antihypertensive effects of some calcium channel blockers. The mechanism appears to be related to an alteration of vascular tone, which is dependent on prostacyclins and other vasodilatory prostanoids. When a nonsteroidal anti-inflammatory drug (NSAID) is added to the regimen of a patient who is already taking a calcium channel blocker, increased blood pressure may result. Also, the clinician should be aware that the risk of hypotension is increased when NSAIDs are withdrawn from the regimen.', 'DDInter', 1767317475, 'Monitoring for altered blood pressure control is recommended.', 'Antagonism', 'Monitoring for altered blood pressure control is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381430, 'Fesoterodine', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of fesoterodine should be considered. Because 5-hydroxymethyl tolterodine is also metabolized by CYP450 2D6, the clinical significance of the interaction may be greater in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 (approximately 7% of Caucasians and less than 2% of Asians and individuals of African descent) who may rely more on the 3A4 metabolic pathway for clearance of the drug.', 'DDInter', 1767317475, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary. Patients should be advised to notify their physician if they experience potential adverse effects of fesoterodine such as irregular heartbeat, blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, gastrointestinal upset, or constipation.', 'Metabolism', 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381431, 'Fidaxomicin', 'Diltiazem', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter. Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.', 'DDInter', 1767317475, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381432, 'Finasteride', 'Diltiazem', 'Moderate', 'Coadministration with diltiazem may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by diltiazem.', 'DDInter', 1767317475, 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever diltiazem is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381433, 'Fingolimod', 'Diltiazem', 'Major', 'The risk of severe bradycardia and atrioventricular (AV) block may be increased during initiation of fingolimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', 'DDInter', 1767317475, 'Fingolimod has not been adequately studied in patients receiving beta-blockers, calcium channel blockers, or digitalis. The possibility of switching to alternative agents that do not slow heart rate or AV conduction should be evaluated by the physician before initiating fingolimod. In patients who cannot switch, overnight continuous ECG monitoring after the first dose is recommended in accordance with the product labeling. The same precautions are applicable if, after the first month of treatment, fingolimod is discontinued for more than two weeks and then restarted, since the effects on heart rate and AV conduction may recur on reintroduction of fingolimod. Within the first 2 weeks of treatment, first-dose procedures are also recommended after interruption of one day or more; during week 3 and 4 of treatment, first-dose procedures are recommended after treatment interruption of more than 7 days. The first dose should always be administered in a setting where resources to appropriately manage symptomatic bradycardia are available.', 'Synergism', 'Fingolimod has not been adequately studied in patients receiving beta-blockers, calcium channel blockers, or digitalis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381434, 'Flecainide', 'Diltiazem', 'Major', 'Limited data indicate that verapamil and flecainide may have additive cardiodepressant effects.Diltiazem may interact with flecainide in a similar manner, although no data are available.', 'DDInter', 1767317475, 'The patient should be monitored closely when either drug is added to the regimen or when dose increases are made.', 'Synergism', 'The patient should be monitored closely when either drug is added to the regimen or when dose increases are made.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381435, 'Flibanserin', 'Diltiazem', 'Major', 'Coadministration with moderate and potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19. High plasma levels of flibanserin may increase the risk of severe hypotension, syncope, and central nervous system depression.', 'DDInter', 1767317475, 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated. When initiating flibanserin following treatment with a moderate or potent CYP450 3A4 inhibitor, the manufacturer recommends waiting until 2 weeks after the last dose of CYP450 3A4 inhibitor. Conversely, if a moderate or potent CYP450 3A4 inhibitor is required during flibanserin therapy, flibanserin should be discontinued at least 2 days before starting the CYP450 3A4 inhibitor.', 'Metabolism', 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381436, 'Fluconazole', 'Diltiazem', 'Moderate', 'Coadministration with azole antifungal agents may increase the plasma concentrations of calcium channel blockers (CCBs), especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most CCBs.', 'DDInter', 1767317475, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with azole antifungal agents. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with azole antifungal agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381437, 'Fludrocortisone', 'Diltiazem', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', 'DDInter', 1767317475, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', 'Patients on prolonged (i.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381438, 'Fluoxetine', 'Diltiazem', 'Moderate', 'Coadministration of fluoxetine with calcium channel blockers may increase their plasma concentrations and the risk of side effects. The mechanism is reduced clearance of the calcium channel blocker due to inhibition of CYP450 3A4-mediated metabolism by fluoxetine. Reports exist for nifedipine, nimodipine, and verapamil. Theoretically, the interaction may also occur with other calcium channel blockers that are CYP450 3A4 substrates.', 'DDInter', 1767317475, 'Monitoring of clinical response and tolerance, including blood pressure, is recommended if fluoxetine is used in combination with calcium channel blockers that are metabolized by CYP450 3A4. Dose reductions may be considered, if necessary. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Monitoring of clinical response and tolerance, including blood pressure, is recommended if fluoxetine is used in combination with calcium channel blockers that are metabolized by CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381439, 'Fluphenazine', 'Diltiazem', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317475, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381440, 'Flurazepam', 'Diltiazem', 'Moderate', 'Coadministration with diltiazem may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by diltiazem.', 'DDInter', 1767317475, 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever diltiazem is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381441, 'Flurbiprofen', 'Diltiazem', 'Moderate', 'Limited data indicate that some cyclooxygenase inhibitors may attenuate the antihypertensive effects of some calcium channel blockers. The mechanism appears to be related to an alteration of vascular tone, which is dependent on prostacyclins and other vasodilatory prostanoids. When a nonsteroidal anti-inflammatory drug (NSAID) is added to the regimen of a patient who is already taking a calcium channel blocker, increased blood pressure may result. Also, the clinician should be aware that the risk of hypotension is increased when NSAIDs are withdrawn from the regimen.', 'DDInter', 1767317475, 'Monitoring for altered blood pressure control is recommended.', 'Antagonism', 'Monitoring for altered blood pressure control is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381442, 'Fluticasone', 'Diltiazem', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the systemic exposure to fluticasone following intranasal administration or oral inhalation. Fluticasone undergoes extensive first-pass and systemic metabolism via CYP450 3A4, thus inhibition of the isoenzyme may significantly increase systemic bioavailability of the drug. However, the extent of interaction may depend on the route of fluticasone administration and the specific formulation.', 'DDInter', 1767317475, 'Monitor for signs and symptoms of hypercorticism if intranasal or orally inhaled fluticasone is coadministered with a moderate inhibitor of CYP450 3A4. Signs and symptoms of hypercorticism include acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Signs and symptoms of adrenal insufficiency include anorexia, hypoglycemia, nausea, vomiting, weight loss, muscle wasting, fatigue, weakness, dizziness, postural hypotension, depression, and adrenal crisis manifested as inability to respond to stress (e.g., illness, infection, surgery, trauma). Systemic glucocorticoids may be necessary until adrenal function recovers.', 'Metabolism', 'Monitor for signs and symptoms of hypercorticism if intranasal or orally inhaled fluticasone is coadministered with a moderate inhibitor of CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381443, 'Fluvoxamine', 'Diltiazem', 'Moderate', 'Concurrent administration of fluvoxamine and diltiazem may cause bradycardia.', 'DDInter', 1767317475, 'The manufacturer recommends caution if these drugs must be used together.', 'Synergism', 'The manufacturer recommends caution if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381444, 'Fosamprenavir', 'Diltiazem', 'Moderate', 'Coadministration with amprenavir or its prodrug, fosamprenavir, may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by amprenavir.', 'DDInter', 1767317475, 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever amprenavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381445, 'Fosaprepitant', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381446, 'Fosinopril', 'Diltiazem', 'Minor', 'Calcium channel blockers and angiotensin converting enzyme (ACE) inhibitors may have additive hypotensive effects. While these drugs are often safely used together, careful monitoring of the systemic blood pressure is recommended during coadministration, especially during the first one to three weeks of therapy.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381447, 'Fosphenytoin', 'Diltiazem', 'Moderate', 'Diltiazem and verapamil may increase plasma phenytoin levels. Toxicity has been reported. The proposed mechanism is inhibition of CYP450 3A4 metabolism. In addition, phenytoin may significantly decrease calcium channel blocker (CCB) serum levels by inducing first-pass metabolism and the systemic clearance. Other hydantoins may participate in these interactions as well.', 'DDInter', 1767317475, 'Close clinical and laboratory observation for evidence of altered effect of both drugs is recommended if a CCB and a hydantoin must be used together. Patients should be advised to notify their physician if they experience symptoms of phenytoin toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia) or loss of effect of their CCB.', 'Metabolism', 'Close clinical and laboratory observation for evidence of altered effect of both drugs is recommended if a CCB and a hydantoin must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381448, 'Fostamatinib', 'Diltiazem', 'Moderate', 'Coadministration of fostamatinib with inhibitors of CYP450 3A4 may increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. In vitro, R406 is also a substrate of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 1767317475, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381449, 'Fostemsavir', 'Diltiazem', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4, P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of temsavir, the active moiety of fostemsavir. Increased temsavir exposure may increase the risk of QT prolongation.', 'DDInter', 1767317475, 'No dosage adjustments are recommended for fostemsavir when used with potent CYP450 3A4, P-gp, and/or BCRP inhibitors. It may be advisable to monitor patients for increased adverse effects such as QT prolongation during coadministration, particularly in the elderly and patients with risk factors for torsade de pointes.', 'Metabolism, Absorption', 'No dosage adjustments are recommended for fostemsavir when used with potent CYP450 3A4, P-gp, and/or BCRP inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381450, 'Gadopentetic acid', 'Diltiazem', 'Moderate', 'Coadministration of calcium channel blockers may increase the cardiovascular effects of ionic X-ray contrast media. The mechanism is unknown.', 'DDInter', 1767317475, 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension. Use of a nonionic contrast agent, such as iopamidol, may be considered instead of ionic contrast where clinically appropriate.', 'Synergism', 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381451, 'Gadoteric acid', 'Diltiazem', 'Moderate', 'Coadministration of calcium channel blockers may increase the cardiovascular effects of ionic X-ray contrast media. The mechanism is unknown.', 'DDInter', 1767317475, 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension. Use of a nonionic contrast agent, such as iopamidol, may be considered instead of ionic contrast where clinically appropriate.', 'Synergism', 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381452, 'Gadoxetic acid', 'Diltiazem', 'Moderate', 'Coadministration of calcium channel blockers may increase the cardiovascular effects of ionic X-ray contrast media. The mechanism is unknown.', 'DDInter', 1767317475, 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension. Use of a nonionic contrast agent, such as iopamidol, may be considered instead of ionic contrast where clinically appropriate.', 'Synergism', 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381453, 'Galantamine', 'Diltiazem', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 1767317475, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381454, 'Garlic', 'Diltiazem', 'Minor', 'Garlic has been found in some studies to lower blood pressure and may theoretically potentiate the effects of hypotensive agents. A meta-analysis of 11 randomized, placebo-controlled studies showed a mean decrease of 4.6 mmHg for systolic blood pressure and no significant effect for diastolic blood pressure in subjects treated with garlic (most often garlic powder 600 to 900 mg/day, providing 3.6 to 5.4 mg of the active component allicin) compared to placebo.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381455, 'Gefitinib', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of gefitinib, which is primarily metabolized by the isoenzyme. According to the product labeling, administration of gefitinib (250 mg single dose) with the potent inhibitor itraconazole (200 mg once a day for 12 days) increased the mean gefitinib systemic exposure (AUC) by 88% in healthy male volunteers.', 'DDInter', 1767317475, 'Caution is advised if gefitinib is administered with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors. Pharmacologic response to gefitinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience possible symptoms of gefitinib toxicity such as severe diarrhea, nausea, dyspnea, cough, and fever.', 'Metabolism', 'Caution is advised if gefitinib is administered with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381456, 'Gilteritinib', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of gilteritinib, which is a substrate of both the isoenzyme and the efflux transporter.', 'DDInter', 1767317475, 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for adverse effects such as QT interval prolongation, pancreatitis, liver transaminase and bilirubin elevations, edema, infections and stomatitis, and the gilteritinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381457, 'Glasdegib', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of glasdegib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when glasdegib is used with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as QT interval prolongation, anemia, febrile neutropenia, thrombocytopenia, hemorrhage, edema and mucositis, and the glasdegib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when glasdegib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381458, 'Granisetron', 'Diltiazem', 'Moderate', 'Coadministration with diltiazem may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by diltiazem.', 'DDInter', 1767317475, 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever diltiazem is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381459, 'Grepafloxacin', 'Diltiazem', 'Moderate', 'Based on in vitro inhibition data, coadministration with grepafloxacin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by grepafloxacin.', 'DDInter', 1767317475, 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever grepafloxacin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381460, 'Griseofulvin', 'Diltiazem', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may significantly decrease the plasma concentrations of diltiazem, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Use of diltiazem with rifampin or any known CYP450 3A4 inducer should be avoided when possible, and alternative therapy considered. Diltiazem dose adjustment should be guided by clinical response if concomitant use with a CYP450 3A4 inducer is necessary.', 'Metabolism', 'Use of diltiazem with rifampin or any known CYP450 3A4 inducer should be avoided when possible, and alternative therapy considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381461, 'Guanfacine', 'Diltiazem', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of guanfacine, which is primarily metabolized by the isoenzyme. The risk of adverse reactions such as hypotension, bradycardia, and sedation may increase.', 'DDInter', 1767317475, 'Caution and dosage adjustment are advised when guanfacine is administered with potent and moderate CYP450 3A4 inhibitors. For extended-release guanfacine, the manufacturers recommend reducing the dosage to half the recommended level during concomitant use . Further dosage adjustments may be required based on patient tolerance and response.', 'Metabolism', 'Caution and dosage adjustment are advised when guanfacine is administered with potent and moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381462, 'Halazepam', 'Diltiazem', 'Moderate', 'Coadministration with diltiazem may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by diltiazem.', 'DDInter', 1767317475, 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever diltiazem is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381463, 'Halofantrine', 'Diltiazem', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of halofantrine, resulting in an increased risk of QT interval prolongation and ventricular arrhythmias. The mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of halofantrine. Halofantrine has been associated with QT interval prolongation, ventricular arrhythmias, and sudden death, even at recommended dosages.', 'DDInter', 1767317475, 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors. The manufacturer recommends performing an ECG before initiating halofantrine therapy and cardiac monitoring during and for 8 to 12 hours after completion of therapy.', 'Metabolism', 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381464, 'Haloperidol', 'Diltiazem', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317475, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381465, 'Halothane', 'Diltiazem', 'Moderate', 'Calcium channel blockers may potentiate the cardiac depressant and hypotensive effects of volatile (halogenated) anesthetics, and vice versa, due to their similar pharmacologic action on the cardiovasculature. Serious consequences of this interaction may include cardiovascular collapse, ventricular impairment, and/or conduction disturbances, particularly in patients with underlying cardiac complications. The interaction may be more likely with verapamil and diltiazem due to their dromotropic and chronotropic effects.', 'DDInter', 1767317475, 'Discontinuation of calcium channel blockers prior to surgery is generally not recommended because of the rebound effect in patients with coronary heart disease or arterial hypertension. However, extreme care must be taken when administering halogenated anesthetics to patients under treatment with calcium channel blockers. Hemodynamic and electrocardiographic monitoring are recommended during the operation and postoperatively, and the dosage of one or both drugs adjusted as necessary.', 'Synergism', 'Discontinuation of calcium channel blockers prior to surgery is generally not recommended because of the rebound effect in patients with coronary heart disease or arterial hypertension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381466, 'Diltiazem', 'Diamorphine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381467, 'Hydromorphone', 'Diltiazem', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381468, 'Hydroxychloroquine', 'Diltiazem', 'Moderate', 'Coadministration with moderate or strong inhibitors of CYP450 2C8 or CYP450 3A4 may increase the plasma concentration of hydroxychloroquine and the risk of toxicities such as QT interval prolongation and ventricular arrhythmias.', 'DDInter', 1767317475, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor. Clinical and laboratory monitoring should be considered whenever a moderate or strong CYP450 3A4 or 2C8 inhibitor is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381469, 'Hydroxyzine', 'Diltiazem', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381470, 'Ibrutinib', 'Diltiazem', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ibrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The dosage of ibrutinib should be adjusted according to the indication in the product labeling whenever it is used in combination with a moderate CYP450 3A4 inhibitor such as amprenavir, aprepitant, atazanavir, ciprofloxacin, crizotinib, darunavir, diltiazem, dronedarone, erythromycin, fluconazole, fosamprenavir, fosaprepitant, imatinib, or verapamil. All patients should be closely monitored for signs of ibrutinib toxicity such as myelosuppression, bleeding, infection, and renal impairment.', 'Metabolism', 'The dosage of ibrutinib should be adjusted according to the indication in the product labeling whenever it is used in combination with a moderate CYP450 3A4 inhibitor such as amprenavir, aprepitant, atazanavir, ciprofloxacin, crizotinib, darunavir, diltiazem, dronedarone, erythromycin, fluconazole, fosamprenavir, fosaprepitant, imatinib, or verapamil.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381471, 'Idarubicin', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', 'DDInter', 1767317475, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381472, 'Idelalisib', 'Diltiazem', 'Moderate', 'Coadministration with idelalisib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. Idelalisib has been shown to be a potent inhibitor of this isoenzyme.', 'DDInter', 1767317475, 'Use of idelalisib should generally be avoided with drugs that are primarily metabolized by CYP450 3A4, particularly those with a narrow therapeutic range (e.g., antiarrhythmics, anticonvulsants, antineoplastics, immunosuppressants) or those that are considered sensitive substrates (e.g., ergot derivatives, statins, oral midazolam, triazolam). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever idelalisib is added to or withdrawn from therapy, if coadministration is required.', 'Metabolism', 'Use of idelalisib should generally be avoided with drugs that are primarily metabolized by CYP450 3A4, particularly those with a narrow therapeutic range (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381473, 'Ifosfamide', 'Diltiazem', 'Moderate', 'Coadministration with diltiazem may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by diltiazem.', 'DDInter', 1767317475, 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever diltiazem is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381474, 'Iloperidone', 'Diltiazem', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317475, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381475, 'Iloprost', 'Diltiazem', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 1767317475, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381476, 'Imatinib', 'Diltiazem', 'Moderate', 'Imatinib is a substrate of the CYP450 3A4 isoenzyme as well as a potent competitive inhibitor of the CYP450 2C9, 2D6 and 3A4 isoenzymes. Some drugs that are known inhibitors of CYP450 3A4 are also metabolized by one or more of the isoenzymes inhibited by imatinib. Theoretically, coadministration of imatinib with those drugs may result in mutually elevated plasma drug concentrations due to competitive and noncompetitive inhibition of CYP450 activities.', 'DDInter', 1767317475, 'The possibility of prolonged and/or increased pharmacologic effects of imatinib, including serious adverse effects such as edema, hematologic toxicity and immunosuppression, should be considered during concomitant therapy, particularly with potent CYP450 3A4 inhibitors such as ketoconazole, itraconazole, ritonavir, nefazodone, and erythromycin. In addition, clinical and laboratory monitoring for potentially increased pharmacologic effects of coadministered medications is recommended, especially those with a narrow therapeutic range.', 'Metabolism', 'The possibility of prolonged and/or increased pharmacologic effects of imatinib, including serious adverse effects such as edema, hematologic toxicity and immunosuppression, should be considered during concomitant therapy, particularly with potent CYP450 3A4 inhibitors such as ketoconazole, itraconazole, ritonavir, nefazodone, and erythromycin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381477, 'Imipramine', 'Diltiazem', 'Moderate', 'Limited data suggest that diltiazem can increase the plasma concentrations of cyclic antidepressants. Data are available for nortriptyline, trimipramine, and imipramine. The proposed mechanism is inhibition of first-pass metabolism by diltiazem. ECG changes (first and second degree heart block) have been reported.', 'DDInter', 1767317475, 'Close monitoring for clinical response and tolerance is recommended whenever diltiazem is added to or discontinued from an antidepressant regimen. Patients should be advised to notify their physician if they experience excessive antidepressant effects such as dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or orthostatic hypotension. Dose adjustments may be necessary.', 'Others', 'Close monitoring for clinical response and tolerance is recommended whenever diltiazem is added to or discontinued from an antidepressant regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381478, 'Indacaterol', 'Diltiazem', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the systemic exposure to indacaterol following oral inhalation, as it is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381479, 'Indinavir', 'Diltiazem', 'Moderate', 'Coadministration with indinavir may increase the plasma concentrations and pharmacologic effects of calcium channel blockers, especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves indinavir inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most calcium channel blockers.', 'DDInter', 1767317475, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with indinavir. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with indinavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381480, 'Indomethacin', 'Diltiazem', 'Moderate', 'Limited data indicate that some cyclooxygenase inhibitors may attenuate the antihypertensive effects of some calcium channel blockers. The mechanism appears to be related to an alteration of vascular tone, which is dependent on prostacyclins and other vasodilatory prostanoids. When a nonsteroidal anti-inflammatory drug (NSAID) is added to the regimen of a patient who is already taking a calcium channel blocker, increased blood pressure may result. Also, the clinician should be aware that the risk of hypotension is increased when NSAIDs are withdrawn from the regimen.', 'DDInter', 1767317475, 'Monitoring for altered blood pressure control is recommended.', 'Antagonism', 'Monitoring for altered blood pressure control is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381481, 'Insulin human', 'Diltiazem', 'Minor', 'single case report suggests that diltiazem may decrease the hypoglycemic effect of insulin. The mechanism of interaction is unknown. While no special precautions appear to be necessary, patients may consider monitoring blood glucose more closely during the first few days or weeks of diltiazem therapy until glucose control is assured.', 'DDInter', 1767317475, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
