INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96065, 'Efavirenz', 'Proguanil', 'Major', 'Coadministration with efavirenz may significantly decrease the plasma concentrations of atovaquone and proguanil. The mechanism of interaction has not been established, but may involve induction of the glucuronidation of atovaquone and the CYP450 2C19-mediated metabolism of proguanil.', NULL, 'Given the potential for treatment failure when atovaquone or atovaquone/proguanil is administered with efavirenz, concomitant use is not recommended.', NULL, 'Given the potential for treatment failure when atovaquone or atovaquone/proguanil is administered with efavirenz, concomitant use is not recommended.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96169/', '[1] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[2] Van Luin M, Van der Ende ME, Richter C, et al. "Lower atovaquone/proguanil concentrations in patients taking efavirenz, lopinavir/ritonavir or atazanavir/ritonavir." AIDS 24 (2010): 1223-6[3] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96066, 'Efavirenz', 'Promazine', 'Major', 'Efavirenz can cause significant QT interval prolongation in patients with the CYP450 2B6 *6/*6 genotype due to reduced metabolic clearance of the drug stemming from decreased levels of functional CYP450 2B6 isoenzyme.', NULL, 'Alternatives to efavirenz should be considered in patients receiving other drugs that can prolong the QT interval.', NULL, 'Alternatives to efavirenz should be considered in patients receiving other drugs that can prolong the QT interval. Patients treated with any medication that can cause QT prolongation should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96170/', '[1] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[2] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363023);
