INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96084, 'Efavirenz', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96188/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96085, 'Efavirenz', 'Repaglinide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of repaglinide, which is metabolized by the isoenzyme in the intestine and liver.', NULL, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, patients treated concomitantly with rifampin may require a higher dosage of repaglinide.', NULL, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, patients treated concomitantly with rifampin may require a higher dosage of repaglinide. Blood glucose should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy in patients stabilized on their existing antidiabetic regimen, and the repaglinide dosage adjusted as necessary. Patients should be advised to contact their physician if they experience loss of glycemic control.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96189/', '[1] Niemi M, Backman JT, Neuvonen M, Neuvonen PJ, Kivisto KT "Rifampin decreases the plasma concentrations and effects of repaglinide." Clin Pharmacol Ther 68 (2000): 495-500[2] Bidstrup TB, Bjornsdottir I, Sidelmann UG, Thomsen MS, Hansen KT "CYP2C8 and CYP3A4 are the principal enzymes involved in the human in vitro biotransformation of the insulin secretagogue repaglinide." Br J Clin Pharmacol 56 (2003): 305-14[3] "Product Information. Prandin (repaglinide)." Novo Nordisk Pharmaceuticals Inc, Princeton, NJ.[4] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[5] Bailey DG, Arnol