INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96279, 'Elagolix', 'Clotrimazole', 'Moderate', 'Coadministration with inhibitors of the OATP 1B1 hepatic uptake transporter, CYP450 3A4 metabolic isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of elagolix. The effect of P-gp inhibitors on the pharmacokinetics of elagolix has not been studied, but some increases in elagolix exposure may occur, as it is a P-gp substrate. Increased exposure to elagolix may increase the risk of serious adverse effects such as bone loss, suicidal ideation and behavior, exacerbation of mood disorders, and hepatic transaminase elevations.', NULL, 'Caution is advised when elagolix is used with OATP 1B1, CYP450 3A4, and/or P-gp inhibitors.', NULL, 'Caution is advised when elagolix is used with OATP 1B1, CYP450 3A4, and/or P-gp inhibitors. Patients should be monitored for potentially altered effects of elagolix following the initiation or discontinuation of these inhibitors, and the elagolix dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96383/', '[1] "Product Information. Orilissa (elagolix)." AbbVie US LLC, North Chicago, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96280, 'Elagolix', 'Conjugated estrogens (topical)', 'Moderate', 'Since endometriosis is fueled by estrogen, coadministration with estrogen-containing medications including combination oral contraceptive pills is expected to reduce the efficacy of elagolix. The effect of progestin-only contraceptives is unknown. Elagolix is a gonadotropin-releasing hormone (GnRH) receptor antagonist that inhibits endogenous GnRH signaling by binding competitively to GnRH receptors in the pituitary gland. Administration of elagolix results in dose-dependent suppression of luteinizing hormone (LH) and follicle-stimulating hormone (FSH), leading to decreased levels of the ovarian sex hormones, estradiol and progesterone.', NULL, 'Women requiring contraception should be advised to use non-hormonal forms of contraception during treatment with elagolix and for one week after its discontinuation.', NULL, 'Women requiring contraception should be advised to use non-hormonal forms of contraception during treatment with elagolix and for one week after its discontinuation.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96384/', '[1] "Product Information. Orilissa (elagolix)." AbbVie US LLC, North Chicago, IL.[2] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[3] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 1767363023);
